mapper.select:
SELECT 0 AS CID, GID , #{gQTY} AS CQTY, GNAME , GBRAND , GPRICE, GIMGS, GCATG 
FROM GOODS
WHERE GID = #{gID}

mapper.update:
UPDATE CART
	SET CQTY = #{gQTY}
WHERE CID = #{samecID}

mapper.select:
SELECT c.CID , c.GID , c.CQTY, g.GNAME , g.GBRAND , g.GPRICE , g.GIMGS, g.GCATG FROM CART c LEFT OUTER JOIN MEMBERS m ON c.USERID = m.userid LEFT OUTER JOIN GOODS g ON c.GID = g.GID WHERE c.CID IN
#{cID}
ORDER BY cID DESC

mapper.delete:
DELETE FROM CART
WHERE cID IN

		#{del_cID}

DELETE FROM CART
WHERE 	CID IN 

		#{cID}
